insert  into `pages_fix`(`id`,`name`,`slug`,`content`,`meta_keyword`,`meta_description`) values (1,'Trang chủ','home','{\"description\":\"<p style=\\\"text-align:center\\\"><img alt=\\\"\\\" src=\\\"https:\\/\\/sachxuasaigon.com\\/wp-content\\/uploads\\/2020\\/01\\/nguyen-thi-minh-khai.jpg\\\" style=\\\"max-width:100%\\\" \\/><\\/p>\\r\\n\\r\\n<p style=\\\"text-align:justify\\\">373 L&yacute; Th\\u01b0\\u1eddng Ki\\u1ec7t&nbsp;t&acirc;\\u0323p trung kha\\u0301 nhi&ecirc;\\u0300u nha\\u0300 sa\\u0301ch l\\u01a1\\u0301n nho\\u0309, ca\\u0309 l&acirc;u \\u0111\\u01a1\\u0300i va\\u0300 hi&ecirc;\\u0323n \\u0111a\\u0323i \\u01a1\\u0309 Sa\\u0300i Go\\u0300n. \\u0110i&ecirc;\\u0309m thu\\u0301 vi\\u0323 \\u01a1\\u0309 \\u0111&acirc;y la\\u0300 ba\\u0323n \\u0111\\u01b0\\u01a1\\u0323c thoa\\u0309i ma\\u0301i \\u0111o\\u0323c sa\\u0301ch mi&ecirc;\\u0303n phi\\u0301 trong ca\\u0301c nha\\u0300 sa\\u0301ch ma\\u0300 kh&ocirc;ng h&ecirc;\\u0300 bi\\u0323 pha\\u0300n na\\u0300n. M&ocirc;\\u0323t s&ocirc;\\u0301 nha\\u0300 sa\\u0301ch l\\u01a1\\u0301n co\\u0301 ca\\u0309 khu \\u0111o\\u0323c sa\\u0301ch da\\u0300nh cho ng\\u01b0\\u01a1\\u0300i l\\u01a1\\u0301n. Nh\\u01b0\\u0303ng gia\\u0301 sa\\u0301ch \\u0111\\u01b0\\u01a1\\u0323c thi&ecirc;\\u0301t k&ecirc;\\u0301 nh\\u01b0 \\u0111\\u01b0\\u01a1\\u0300ng h&acirc;\\u0300m nho\\u0309 \\u01a1\\u0309 khu truy&ecirc;\\u0323n tranh da\\u0300nh cho tre\\u0309 con ng&ocirc;\\u0300i \\u0111o\\u0323c, ho\\u0103\\u0323c co\\u0301 n\\u01a1i chi\\u0309 m&ocirc;\\u0323t va\\u0300i chi&ecirc;\\u0301c gh&ecirc;\\u0301 x&ecirc;\\u0301p g&ocirc;\\u0303 nho nho\\u0309 trong ti&ecirc;\\u0323m sa\\u0301ch be\\u0301 xi\\u0301u l&acirc;u \\u0111\\u01a1\\u0300i b&ecirc;n \\u0111\\u01b0\\u01a1\\u0300ng. Kh&ocirc;ng chi\\u0309 ba\\u0301n sa\\u0301ch, nh\\u01b0\\u0303ng nha\\u0300 sa\\u0301ch na\\u0300y co\\u0300n ba\\u0301n r&acirc;\\u0301t nhi&ecirc;\\u0300u v\\u0103n pho\\u0300ng ph&acirc;\\u0309m, \\u0111&ocirc;\\u0300 l\\u01b0u ni&ecirc;\\u0323m d&ecirc;\\u0303 th\\u01b0\\u01a1ng da\\u0300nh cho ho\\u0323c sinh &ndash; sinh vi&ecirc;n n&ecirc;n m&ocirc;\\u0303i mu\\u0300a t\\u01b0\\u0323u tr\\u01b0\\u01a1\\u0300ng, \\u0111oa\\u0323n \\u0111\\u01b0\\u01a1\\u0300ng ng\\u0103\\u0301n na\\u0300y lu&ocirc;n t&acirc;\\u0301p n&acirc;\\u0323p ng\\u01b0\\u01a1\\u0300i ghe\\u0301 th\\u0103m.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align:justify\\\">S&aacute;ch c\\u0169 kh&ocirc;ng nh\\u01b0 \\u0111a ph\\u1ea7n h&agrave;ng h&oacute;a th&ocirc;ng th\\u01b0\\u1eddng c&agrave;ng \\u0111\\u1ec3 l&acirc;u c&agrave;ng m\\u1ea5t gi&aacute;. Ng\\u01b0\\u1ee3c l\\u1ea1i, s&aacute;ch c\\u0169 c&agrave;ng c\\u0169 x\\u01b0a v\\u1ec1 m\\u1eb7t th\\u1eddi gian, c&agrave;ng &iacute;t v\\u1ec1 m\\u1eb7t s\\u1ed1 l\\u01b0\\u1ee3ng l\\u1ea1i c&agrave;ng qu&yacute; hi\\u1ebfm v&agrave; c&oacute; gi&aacute; cao. L\\u1ea7n gi\\u1edf nh\\u1eefng trang s&aacute;ch c\\u0169, d&ugrave; gi\\u1ea5y c&oacute; th\\u1ec3 \\u0111en, c&oacute; th\\u1ec3 nh&aacute;m, ch\\u1eef kh&oacute; \\u0111\\u1ecdc, g&aacute;y long hay b&igrave;a s\\u1eddn m&eacute;p nh\\u01b0ng l\\u1ea1i c&oacute; c&aacute;i th&uacute; ri&ecirc;ng c\\u1ee7a n&oacute;, kh&ocirc;ng ch\\u1ec9 t\\u1eeb gi&aacute; tr\\u1ecb n\\u1ed9i dung, m&agrave; d\\u1ea5u th\\u1eddi gian c\\u0169ng in \\u0111\\u1ecdng tr&ecirc;n \\u0111&oacute;.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align:justify\\\">V&agrave; t\\u1ea5t nhi&ecirc;n, v\\u1edbi th\\u1eddi \\u0111\\u1ea1i c&ocirc;ng ngh\\u1ec7 s\\u1ed1, b\\u1ea1n v\\u1eabn c&oacute; th\\u1ec3 ng\\u1ed3i t\\u1ea1i nh&agrave; m&agrave; v\\u1eabn c&oacute; th\\u1ec3 mua nh\\u1eefng cu\\u1ed1n&nbsp;s&aacute;ch c\\u0169 gi&aacute; r\\u1ebb&nbsp;online, ho\\u1eb7c \\u0111ang ph&acirc;n v&acirc;n kh&ocirc;ng bi\\u1ebft mua s&aacute;ch c\\u0169 gi&aacute; r\\u1ebb \\u1edf \\u0111&acirc;u. H&atilde;y li&ecirc;n h\\u1ec7 ngay v\\u1edbi Nh&agrave; s&aacute;ch c\\u0169 Kim, ch&uacute;ng t&ocirc;i chuy&ecirc;n mua b&aacute;n c&aacute;c lo\\u1ea1i s&aacute;ch c\\u0169 online,&nbsp;s&aacute;ch x\\u01b0a tr\\u01b0\\u1edbc 1975, ti\\u1ec3u thuy\\u1ebft c\\u0169, t\\u1eeb \\u0111i\\u1ec3n, s&aacute;ch kinh t\\u1ebf,&nbsp;s&aacute;ch v\\u0103n h\\u1ecdc, s&aacute;ch k\\u1ef9 n\\u0103ng s\\u1ed1ng v\\u1edbi ch\\u1ea5t l\\u01b0\\u1ee3ng t\\u1ed1t v&agrave; gi&aacute; r\\u1ebb t\\u1ea1i TP.HCM.<\\/p>\\r\\n\"}','Cửa hàng Truyện tranh TCTRUYEN.COM','Cung cấp các loại sách, truyện tranh nhiều thể loại'),(2,'Liên hệ','contact','{\"description\":\"<h1 style=\\\"text-align:center\\\">C\\u1eecA H&Agrave;NG TRUY\\u1ec6N TRANH TCTRUYEN.COM<\\/h1>\\r\\n\\r\\n<p>&nbsp;<\\/p>\\r\\n\\r\\n<p><img alt=\\\"\\\" src=\\\"http:\\/\\/nonmu.vn\\/upload\\/images\\/baogia-2.jpg\\\" style=\\\"float:left\\\" \\/><\\/p>\\r\\n\\r\\n<ul>\\r\\n\\t<li>\\u0110\\u1ecba ch\\u1ec9: 373 L&yacute; th\\u01b0\\u1eddn ki\\u1ec7t - Ph\\u01b0\\u1eddng&nbsp;8 - Q.T&acirc;n B&igrave;nh - TP.H\\u1ed3 Ch&iacute; Minh<\\/li>\\r\\n\\t<li>S\\u1ed1 \\u0111i\\u1ec7n tho\\u1ea1i : 0938131034<\\/li>\\r\\n\\t<li>Email: tctruyen94@gmail.com<\\/li>\\r\\n\\t<li>website: <a href=\\\"https:\\/\\/tctruyen.com\\\">tctruyen.com<\\/a><\\/li>\\r\\n\\t<li>Gi\\u1edd l&agrave;m vi\\u1ec7c: 24\\/7<\\/li>\\r\\n<\\/ul>\\r\\n\",\"google_map\":\"<iframe src=\\\"https:\\/\\/www.google.com\\/maps\\/embed?pb=!1m18!1m12!1m3!1d3919.400933286217!2d106.65308481391988!3d10.780573092318695!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3175293555b87231%3A0x50f517e71462e7f8!2zMzczIEzDvSBUaMaw4budbmcgS2nhu4d0LCBQaMaw4budbmcgNywgVMOibiBCw6xuaCwgSOG7kyBDaMOtIE1pbmgsIFZp4buHdCBOYW0!5e0!3m2!1svi!2s!4v1561439065875!5m2!1svi!2s\\\" height=\\\"450\\\" frameborder=\\\"0\\\" style=\\\"border:0;width: 100%\\\" allowfullscreen=\\\"\\\"><\\/iframe>\"}','liên hệ','liên hệ');

/*Table structure for table `posts` */

DROP TABLE IF EXISTS `posts`;

CREATE TABLE `posts` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `slug` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `image` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `content` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `status` int(11) DEFAULT NULL,
  `category` int(11) DEFAULT NULL,
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  `created_by` int(11) DEFAULT NULL,
  `updated_by` int(11) DEFAULT NULL,
  `meta_keyword` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

