/*Table structure for table `product` */

DROP TABLE IF EXISTS `product`;

CREATE TABLE `product` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sku` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `slug` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `price` int(11) DEFAULT NULL,
  `sale_price` int(11) DEFAULT NULL,
  `sale_percent` int(11) DEFAULT NULL,
  `image` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `image_list` varchar(510) COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `content` text COLLATE utf8_unicode_ci DEFAULT NULL,
  `status` int(11) DEFAULT NULL,
  `type` tinyint(1) DEFAULT NULL COMMENT '1: hết hàng, 0: còn hàng',
  `category` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'danh mục sản phẩm',
  `qty` int(11) NOT NULL DEFAULT 0 COMMENT 'Số lượng hàng',
  `buy` int(11) NOT NULL DEFAULT 0 COMMENT 'đã mua',
  `material_id` int(11) DEFAULT NULL COMMENT 'Chất liệu',
  `color_id` int(11) DEFAULT NULL COMMENT 'màu sắc',
  `is_featured` tinyint(1) DEFAULT NULL COMMENT 'SP nỗi bật',
  `is_new` tinyint(1) DEFAULT NULL COMMENT 'SP mới',
  `is_saleoff` tinyint(1) DEFAULT NULL,
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL,
  `created_by` int(11) DEFAULT NULL,
  `updated_by` int(11) DEFAULT NULL,
  `meta_keyword` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `product` */

insert  into `product`(`id`,`sku`,`title`,`slug`,`price`,`sale_price`,`sale_percent`,`image`,`image_list`,`description`,`content`,`status`,`type`,`category`,`qty`,`buy`,`material_id`,`color_id`,`is_featured`,`is_new`,`is_saleoff`,`created_at`,`updated_at`,`created_by`,`updated_by`,`meta_keyword`,`meta_description`) values (2,NULL,'7 Viên Ngọc Rồng','non-ket',40000,NULL,NULL,'1603269414_7vien.png','[\"1562481232_(450x450)_non_son_mc001a_xh_1.jpg\",\"1562481232_(450x450)_non_son_mc001a_xh_3.jpg\",\"1562481232_(450x450)_non_son_mc001a_xh_8.jpg\",\"1562481232_(450x450)_non-son-mc001a-xh-2.jpg\"]',NULL,'<p><strong>M&Ocirc; TẢ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>Chất liệu d&ugrave; phối da thật cao cấp.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Form d&aacute;ng sang trọng, s&agrave;nh điệu.</p>\r\n\r\n<p><strong>ĐỐI TƯỢNG:</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nam v&agrave; nữ.</p>\r\n\r\n<p><strong>V&Ograve;NG ĐẦU&nbsp; :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>51 cm &ndash; 58 cm (Mức chuẩn: 54 cm &ndash; 57 cm).</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"3\">\r\n			<p><strong>CHẤT LIỆU</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Vải ch&iacute;nh</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Vải d&ugrave; cao cấp nhập khẩu từ Nhật Bản.</p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li>Vải d&ugrave; được dệt từ sợi tổng hợp, t&ugrave;y v&agrave;o chất liệu, h&agrave;m lượng v&agrave; c&aacute;ch dệt m&agrave; vải sẽ c&oacute; những thuộc t&iacute;nh v&agrave; chất lượng kh&aacute;c nhau.</li>\r\n				<li>Vải d&ugrave; N&oacute;n Sơn nhập về được dệt, nhuộm m&agrave;u bằng c&ocirc;ng nghệ hiện đại tạo ra m&agrave;u sắc v&agrave; chất lượng tuyệt hảo.</li>\r\n				<li>Vải d&ugrave; c&oacute; độ bền cao, nhẹ, tho&aacute;ng m&aacute;t, kh&ocirc;ng thấm nước, &iacute;t b&aacute;m bụi, dễ giặt, độ bền m&agrave;u cao khi tiếp x&uacute;c với bức xạ.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Chất liệu phối</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Da b&ograve; 100% nhập khẩu từ Italia.</p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li>Với c&aacute;c phương ph&aacute;p thuộc da truyền thống c&oacute; từ l&acirc;u đời, kết hợp c&ugrave;ng c&ocirc;ng nghệ, m&aacute;y m&oacute;c &ndash; trang thiết bị hiện đại, da thuộc từ Italia lu&ocirc;n đứng đầu thế giới.</li>\r\n				<li>Những sản phẩm&nbsp; da thuộc N&oacute;n Sơn nhập về từ Italia đều trải qua quy tr&igrave;nh kiểm tra nghi&ecirc;m ngặt về chất lượng v&agrave; m&agrave;u sắc, ph&ugrave; hợp với xu thế thời trang hiện đại</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Vải l&oacute;t</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Vải si cotton cao cấp nhập khẩu từ H&agrave;n Quốc.</p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li>Vải cotton được dệt từ nguy&ecirc;n liệu ch&iacute;nh l&agrave; sợi c&acirc;y b&ocirc;ng kết hợp với nhiều nguy&ecirc;n phụ liệu thi&ecirc;n nhi&ecirc;n v&agrave; nh&acirc;n tạo. T&ugrave;y v&agrave;o tỉ lệ v&agrave; phương ph&aacute;p dệt m&agrave; vải c&oacute; những thuộc t&iacute;nh v&agrave; chất lượng kh&aacute;c nhau.</li>\r\n				<li>Vải 100% cotton cao cấp N&oacute;n Sơn nhập về dựa tr&ecirc;n c&ocirc;ng nghệ dệt nhuộm hiện đại cho ra những m&agrave;u sắc v&agrave; chất lượng vượt trội như: mềm mại, thấm h&uacute;t mồ h&ocirc;i tốt, mang lại cảm gi&aacute;c thoải m&aacute;i.</li>\r\n				<li>Vải 100% cotton th&acirc;n thiện với người d&ugrave;ng, kh&ocirc;ng g&acirc;y k&iacute;ch ứng da v&agrave; c&aacute;c t&aacute;c dụng phụ kh&aacute;c.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Phụ kiện &ndash; kh&oacute;a, n&uacute;t.</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Kh&oacute;a &ndash; lỗ gi&oacute;, l&agrave;m từ hợp kim kh&ocirc;ng gỉ, nhập khẩu từ Đ&agrave;i Loan</p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li>Được sản xuất bằng hợp kim kh&ocirc;ng gỉ cao cấp, trải qua c&ocirc;ng nghệ xử l&yacute; bề mặt ti&ecirc;n tiến tăng độ bền m&agrave;u, độ cứng.</li>\r\n				<li>Kh&oacute;a, lỗ th&ocirc;ng gi&oacute; trước khi gắn v&agrave;o sản phẩm N&oacute;n Sơn đều qua quy tr&igrave;nh kiểm tra chất lượng nghi&ecirc;m ngặt c&ugrave;ng b&agrave;n tay của những người thợ l&agrave;nh nghề tạo ra những mẫu m&atilde; sản phẩm c&oacute; chất lượng&nbsp; vượt trội.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">\r\n			<p><strong>HƯỚNG DẪN SỬ DỤNG &ndash; BẢO QUẢN</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>QUY TR&Igrave;NH GIẶT</strong></p>\r\n\r\n			<p><strong>(TRƯỜNG HỢP KH&Ocirc;NG GIẶT HẤP)</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>BẢO QUẢN</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li>Bảo quản sản phẩm nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</li>\r\n				<li>Trường hợp kh&aacute;ch h&agrave;ng ra nhiều mồ h&ocirc;i, n&ecirc;n giặt sản phẩm ngay sau khi sử dụng.</li>\r\n			</ul>\r\n			</td>\r\n			<td rowspan=\"2\">\r\n			<p><strong><em>Bước 1:</em></strong><em>&nbsp;Th&aacute;o đu&ocirc;i da ra khỏi kh&oacute;a, nh&uacute;ng v&agrave;o nước cho n&oacute;n ướt đều.</em></p>\r\n\r\n			<p><strong><em>Bước 2:</em></strong><em>&nbsp;Pha thật lo&atilde;ng bột giặt &iacute;t chất tẩy (x&agrave; b&ocirc;ng giặt đồ) v&agrave;o nước theo tỉ lệ 1 muỗng c&agrave; ph&ecirc; bột giặt với 2 l&iacute;t nước. H&ograve;a tan bột giặt sau đ&oacute; cho n&oacute;n v&agrave;o.</em></p>\r\n\r\n			<p><strong><em>Bước 3:</em></strong><em>&nbsp;D&ugrave;ng b&agrave;n chải l&ocirc;ng mềm (b&agrave;n chải đ&aacute;nh răng) ch&agrave; nhẹ l&ecirc;n vết bẩn v&agrave; giặt nhanh (tuyệt đối kh&ocirc;ng ng&acirc;m sản phẩm v&agrave; kh&ocirc;ng d&ugrave;ng b&agrave;n chải l&ecirc;n da).</em></p>\r\n\r\n			<p><strong><em>Bước 4:</em></strong><em>&nbsp;Xả lại với nước sạch, cho v&agrave;o t&uacute;i giặt v&agrave; bỏ v&agrave;o m&aacute;y giặt chọn chế độ vắt kh&ocirc; (từ 7 &ndash; 13 ph&uacute;t). Sau đ&oacute; phơi dưới &aacute;nh nắng nhẹ.</em></p>\r\n\r\n			<p><strong><em>Lưu &yacute;:</em></strong></p>\r\n\r\n			<ul>\r\n				<li><em>C&aacute;c c&ocirc;ng đoạn tr&ecirc;n diễn ra li&ecirc;n tục.</em></li>\r\n				<li><em>Trước khi phơi d&ugrave;ng tay nhẹ nh&agrave;ng đẩy ch&oacute;p n&oacute;n v&agrave; vuốt&nbsp; đu&ocirc;i da cho thật thẳng.</em></li>\r\n				<li><em>Sử dụng kẹp phơi v&agrave;o đai b&ecirc;n trong n&oacute;n (vị tr&iacute; giữa lưỡi trai), sao cho lưỡi n&oacute;n hướng ph&iacute;a tr&ecirc;n v&agrave; d&acirc;y đu&ocirc;i hướng ph&iacute;a dưới.</em></li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>C&Aacute;CH GIẶT</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li>Sản phẩm phối da thật Kh&aacute;ch h&agrave;ng n&ecirc;n giặt hấp v&igrave; đặc th&ugrave; da tự nhi&ecirc;n thường xảy ra t&igrave;nh trạng lem m&agrave;u khi tiếp x&uacute;c với nước.</li>\r\n				<li>Trong trường hợp Kh&aacute;ch h&agrave;ng kh&ocirc;ng c&oacute; điều kiện giặt hấp sản phẩm, Qu&yacute; kh&aacute;ch vui l&ograve;ng thực hiện đ&uacute;ng quy tr&igrave;nh giặtđể tr&aacute;nh l&agrave;m mất form d&aacute;ng chuẩn, lem, bạc m&agrave;u da, vải.</li>\r\n				<li>Kh&ocirc;ng ng&acirc;m sản phẩm.</li>\r\n				<li>Kh&ocirc;ng giặt chung sản phẩm với quần &aacute;o v&agrave; c&aacute;c chất liệu vải kh&aacute;c.</li>\r\n				<li>Kh&ocirc;ng sử dụng dầu gội, sữa tắm, dầu xả, chất tẩy để giặt sản phẩm.</li>\r\n				<li>Sau khi giặt xong Qu&yacute; kh&aacute;ch vui l&ograve;ng mang sản phẩm đến cửa h&agrave;ng N&oacute;n Sơn để ủi lại.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n',1,0,'14,10,9,8',0,0,NULL,NULL,NULL,NULL,NULL,'2019-07-07 